import React from 'react';

const Footer = () => {
  return (
    <footer style={footerStyles}>
      <div style={containerStyles}>
        <div style={rowStyles}>
          <div style={col4Styles}>
            <section style={footerSectionStyles}>
              <h3>Services</h3>
              <ul style={ulStyles}>
                <li>SEO</li>
                <li>Social Media Marketing</li>
                <li>UI/UX</li>
                <li>Website Design</li>
                <li>Mobile Application Development</li>
                <li>Shopify Website Development</li>
                <li>Manual Testing Services</li>
                <li>Automation Testing Services</li>
              </ul>
            </section>
          </div>
          <div style={col4Styles}>
            <section style={footerSectionStyles}>
              <h3>Industries</h3>
              <ul style={ulStyles}>
                <li>Education</li>
                <li>Health Care</li>
                <li>Retail & E-Commerce</li>
                <li>Renewable Energy</li>
                <li>All</li>
              </ul>
            </section>
          </div>
          <div style={col4Styles}>
            <section style={footerSectionStyles}>
              <h3>Platforms</h3>
              <ul style={ulStyles}>
                <li>Microsoft Services</li>
                <li>AWS</li>
                <li>Google Cloud</li>
                <li>Wordpress</li>
                <li>Magento</li>
                <li>Office 365 & Teams</li>
              </ul>
            </section>
          </div>
          <div style={col4Styles}>
            <section style={footerSectionStyles}>
              <h3>Subscribe To Our Newsletter</h3>
              <p style={pStyles}>To get the latest news and latest updates.</p>
              <form style={formStyles}>
                <input type="email" placeholder="Email Address" style={inputStyles} />
                <button type="submit" style={buttonStyles}>SUBSCRIBE</button>
              </form>
            </section>
             <section style={{...footerSectionStyles, ...socialMediaStyles}}>
                <ul style={socialMediaUlStyles}>
                  <li><a href="#" style={socialLinkStyles}><i className="fab fa-linkedin-in"></i></a></li>
                  <li><a href="#" style={socialLinkStyles}><i className="fab fa-facebook-f"></i></a></li>
                  <li><a href="#" style={socialLinkStyles}><i className="fab fa-instagram"></i></a></li>
                </ul>
            </section>
          </div>
        </div>
      </div>
       <div style={cookieDisclaimerStyles}>
            <p>We use Cookies in this website to give you the best experience of our website. To find out more, read our Cookie Policy and Privacy Policy.</p>
        </div>
    </footer>
  );
};

const ChatWidget = () => {
    return (
        <div style={chatWidgetStyles}>
            <div style={chatHeaderStyles}>
                Hey there!
            </div>
            <div style={chatBodyStyles}>
              How may I help you today?
            </div>
            <button style={chatButtonStyles}>
              <i className="far fa-comment-dots"></i> {/* Use a suitable chat icon */}
            </button>
        </div>
    )
}

const App = () => {
  return (
    <div style={appContainerStyles}>
      {/* Your other page content goes here */}
      <Footer />
      <ChatWidget/>
    </div>
  );
};

// Styles
const appContainerStyles = {
  display: 'flex',
  flexDirection: 'column',
  minHeight: '100vh',
};

const footerStyles = {
  backgroundColor: '#051e34',
  color: '#fff',
  paddingTop: '50px',
  paddingBottom: '20px',
  marginTop: 'auto',
  position: 'relative',
  width: '100%',
};

const containerStyles = {
  maxWidth: '1200px',
  margin: '0 auto',
  paddingLeft: '16px',
  paddingRight: '16px',
};

const rowStyles = {
  display: 'flex',
  flexWrap: 'wrap',
  margin: '0 -15px',
};

const col4Styles = {
  flex: '0 0 100%',
  maxWidth: '100%',
  paddingLeft: '15px',
  paddingRight: '15px',
  boxSizing: 'border-box',
  '@media (min-width: 768px)': {
    flex: '0 0 50%',
    maxWidth: '50%',
  },
  '@media (min-width: 992px)': {
    flex: '0 0 25%',
    maxWidth: '25%',
  },
};

const footerSectionStyles = {
  marginBottom: '30px',
};

const ulStyles = {
  listStyle: 'none',
  padding: 0,
  margin: 0,
};

const pStyles = {
    marginBottom: '1rem',
}

const formStyles = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    '@media (min-width: 768px)': {
        flexDirection: 'row',
    }
}

const inputStyles = {
  padding: '10px',
  marginBottom: '10px',
  border: '1px solid #ccc',
  borderRadius: '5px',
  width: '100%',
  maxWidth: '250px',
  marginRight: '0',
  '@media (min-width: 768px)': {
    marginRight: '10px',
    marginBottom: '0',
    width: 'auto',
  }
};

const buttonStyles = {
  padding: '10px 15px',
  backgroundColor: '#007bff',
  color: '#fff',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
  transition: 'background-color 0.3s ease',
  '&:hover': {
    backgroundColor: '#0056b3',
  },
};

const socialMediaStyles = {

}
const socialMediaUlStyles = {
    display: 'flex',
    paddingLeft: '0px',
    listStyle: 'none',
}

const socialLinkStyles = {
  display: 'inline-flex',
  alignItems: 'center',
  justifyContent: 'center',
  width: '40px',
  height: '40px',
  borderRadius: '50%',
  backgroundColor: '#3b5998',  // Default to Facebook color.
  marginRight: '10px',
  transition: 'background-color 0.3s ease',
  color: 'white',
  fontSize: '18px',
  textDecoration: 'none',

  '&:hover': {
    opacity: 0.8,
