socialLinkStyles['&:nth-child(1):hover'] = { backgroundColor: '#004a82' };


const cookieDisclaimerStyles = {
  backgroundColor: '#f0f0f0',
  color: '#333',
  padding: '15px',
  textAlign: 'center',
  fontSize: '0.9rem',
  borderTop: '1px solid #ccc',
};

const chatWidgetStyles = {
    position: 'fixed',
    bottom: '20px',
    right: '20px',
    display: 'flex',
    flexDirection: 'column',
