const chatButtonStyles = {
  backgroundColor: '#007bff',
  color: '#fff',
  border: 'none',
  borderRadius: '50%',
  width: '60px',
  height: '60px',
  cursor: 'pointer',
  fontSize: '24px',
  boxShadow: '0 2px 5px rgba(0,0,0,0.3)',
  transition: 'background-color 0.3s ease',
  '&:hover': {
    backgroundColor: '#0056b3',
