import React from 'react';
import './styles.css'; // Import the CSS file

const Footer = () => {
  return (
    <footer className="footer">
      <div className="container">
        <div className="row">
          <div className="col-4">
            <section className="footer-section">
              <h3>Services</h3>
              <ul>
                <li>SEO</li>
                <li>Social Media Marketing</li>
                <li>UI/UX</li>
                <li>Website Design</li>
                <li>Mobile Application Development</li>
                <li>Shopify Website Development</li>
                <li>Manual Testing Services</li>
                <li>Automation Testing Services</li>
              </ul>
            </section>
          </div>
          <div className="col-4">
            <section className="footer-section">
              <h3>Industries</h3>
              <ul>
                <li>Education</li>
                <li>Health Care</li>
                <li>Retail & E-Commerce</li>
                <li>Renewable Energy</li>
                <li>All</li>
              </ul>
            </section>
          </div>
          <div className="col-4">
            <section className="footer-section">
              <h3>Platforms</h3>
              <ul>
                <li>Microsoft Services</li>
                <li>AWS</li>
                <li>Google Cloud</li>
                <li>Wordpress</li>
                <li>Magento</li>
                <li>Office 365 & Teams</li>
              </ul>
            </section>
          </div>
          <div className="col-4">
            <section className="footer-section">
              <h3>Subscribe To Our Newsletter</h3>
              <p>To get the latest news and latest updates.</p>
              <form>
                <input type="email" placeholder="Email Address" />
                <button type="submit">SUBSCRIBE</button>
              </form>
            </section>
             <section className="footer-section social-media">
                <ul>
                  <li><a href="#"><i className="fab fa-linkedin-in"></i></a></li>
                  <li><a href="#"><i className="fab fa-facebook-f"></i></a></li>
                  <li><a href="#"><i className="fab fa-instagram"></i></a></li>
                </ul>
            </section>
          </div>
        </div>
      </div>
       <div className="cookie-disclaimer">
            <p>We use Cookies in this website to give you the best experience of our website. To find out more, read our Cookie Policy and Privacy Policy.</p>
        </div>
    </footer>
  );
};

const ChatWidget = () => {
    return (
        <div className="chat-widget">
            <div className="chat-header">
                Hey there!
            </div>
            <div className="chat-body">
              How may I help you today?
            </div>
            <button className="chat-button">
              <i className="far fa-comment-dots"></i> {/* Use a suitable chat icon */}
            </button>
        </div>
    )
}

const App = () => {
  return (
    <div className="app-container">
      {/* Your other page content goes here */}
      <Footer />
      <ChatWidget/>
    </div>
  );
};

export default App;
